var contentful = require("contentful");

var client = contentful.createClient({
  space: "dlipnuauv0sj",
  accessToken: "wE7IZ7NvcSRJkr8slwMR5ZM6zEByjDgC7y1nEn36gx4",
});

export const getAbout = () => (dispatch) => {
  client.getEntries({ content_type: "about" }).then((resp) =>
    dispatch({
      type: "REQUEST_ABOUT_SUCCESS",
      payload: resp.items[0].fields.about,
    })
  );
};

export const getBlog = () => (dispatch) => {
  fetch(
    `https://www.googleapis.com/blogger/v3/blogs/9085604028025620950/posts?key=AIzaSyChWrKyLjsVyYj42CKBstkjrOnKWwPLqDs`
  )
    .then((resp) => resp.json())
    .then((data) =>
      dispatch({
        type: "REQUEST_JOURNAL_SUCCESS",
        payload: data.items,
      })
    );
};

export const timeChange = (time) => {
  return {
    type: "TIME",
    payload: time,
  };
};

export const navToggle = (position) => {
  return {
    type: "POSITION",
    payload: position,
  };
};
