import React, { useState, useEffect } from "react";
import "./styles/style.css";

const Images = () => {
  const [feed, setFeed] = useState([]);
  const [number, setNumber] = useState(0);

  useEffect(() => {
    fetch(
      `https://api.tumblr.com/v2/blog/t:ludyybtv8EyHxeJzIanq3w/info?api_key=1K5kBBtN3I6XMgfAB6qU4kQieFrKuz2abpFw21YxeauKQcUW2s`
    )
      .then((res) => res.json())
      .then((data) => setNumber(Math.ceil(data.response.blog.posts / 50)));

    for (let i = 0; i < number; i++) {
      fetch(
        `https://api.tumblr.com/v2/blog/t:ludyybtv8EyHxeJzIanq3w/posts?api_key=1K5kBBtN3I6XMgfAB6qU4kQieFrKuz2abpFw21YxeauKQcUW2s&limit=50&offset=${
          i * 50
        }`
      )
        .then((res) => res.json())
        .then((data) =>
          setFeed((prevValue) => {
            return [...prevValue, ...data.response.posts];
          })
        );
    }
  }, [number]);

  return (
    <div className="images">
      {feed.map((image, i) => (
        <div key={i} className="flexChild">
          <img key={i} src={image.link_url} alt=""></img>
        </div>
      ))}
    </div>
  );
};

export default Images;
