import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getImages } from "../../actions/index.js";
import nature from "./music/nature.mp3";
import Loading from "../Loading/Loading";
import "./styles/style.css";

const Images = () => {
  const dispatch = useDispatch();
  let feed = useSelector((state) => state.fetchImages.images);

  function play() {
    const audio = document.getElementsByClassName("audio-element")[0];
    audio.play();
    audio.volume = "0.2";
  }

  useEffect(() => {
    dispatch(getImages());
  }, [dispatch]);

  return feed.length === 0 ? (
    <div>
      <Loading />
      <audio className="audio-element" loop>
        <source src={nature} type="audio/mp3"></source>
      </audio>
    </div>
  ) : (
    <div className="images">
      <audio className="audio-element" loop>
        <source src={nature} type="audio/mp3"></source>
      </audio>
      {feed.map((image, i) => (
        <img key={i} src={image.link_url} alt=""></img>
      ))}
    </div>
  );
};

export default Images;
