const search = (state = { opacity: "0", display: "none" }, action) => {
  switch (action.type) {
    case "VISIBLE":
      return {
        ...state,
        visibilty: action.payload.opacity,
        display: action.payload.display,
      };

    default:
      return state;
  }
};

export default search;
