const search = (
  state = { opacity: "0", width: "0", display: "none" },
  action
) => {
  switch (action.type) {
    case "VISIBLE":
      return {
        ...state,
        opacity: action.payload.opacity,
        width: action.payload.width,
        display: action.payload.display,
      };

    default:
      return state;
  }
};

export default search;
